import math
import matplotlib.pyplot as plt

# riflessione asse y
def rifl_asse_y(points):
    for i in range(len(points)):
        x = -points[i][0]
        y = points[i][1]
        if x != 0:
            points.append((x,y))

# riflessione asse x
def rifl_asse_x(points):
    for i in range(len(points)):
        x = points[i][0]
        y = -points[i][1]
        if y != 0:
            points.append((x,y))

# vertici triangoli equilateri di lato 1 in un esagono inscritto in una circonferenza
def vertici_triangoli_esagono(r):
    vertici = []

    # vertici nel primo quadrante
    u_y = math.sqrt(3)/2

    for i in range(r):
        pos_x = i*0.5
        pos_y = i*u_y

        while pos_x >= 0:
            vertici.append((pos_x,pos_y))
            pos_x -= 1

        pos_x = (i+1)*0.5
        pos_y -= u_y
        while pos_y >= 0:
            vertici.append((pos_x,pos_y))
            pos_y -= u_y
            pos_x += 0.5

    rifl_asse_y(vertici)
    rifl_asse_x(vertici)

    print(vertici)
    print(len(vertici))

    plt.scatter([v[0] for v in vertici], [v[1] for v in vertici])
    plt.show()


# vertici triangoli equilateri di lato 1 in una circonferenza
def vertici_triangolo_cherchio(r: float):
    vertici = []
    square = r*r
    u_y = math.sqrt(3)

    # vertici nel primo quadrante
    pos_x = 0
    while pos_x <= r:
        pos_y = 0
        limit = math.sqrt(square - pos_x*pos_x)
        while pos_y <= limit:
            vertici.append((pos_x,pos_y))
            pos_y += u_y

        pos_x += 1

    pos_x = 0.5
    while pos_x <= r:
        pos_y = u_y*0.5
        limit = math.sqrt(square - pos_x*pos_x)
        while pos_y <= limit:
            vertici.append((pos_x,pos_y))
            pos_y += u_y
        pos_x += 1

    rifl_asse_y(vertici)
    rifl_asse_x(vertici)

    print([(v[0]/2,v[1]/2) for v in vertici])
    print(len(vertici))

    plt.scatter([v[0] for v in vertici], [v[1] for v in vertici])
    plt.show()


vertici_triangolo_cherchio(2)

